example_usage:
# Using sharpen
```
{
    "sharpen": True,
}
```

# Using sharpen with custom value
```
{
    "sharpen": 10,
}
```

# unsharp mask with custom params value
```
{
    "unsharp_mask": {
        "radius": 2,
        "sigma": 2,
        "amount": 0.8,
        "threshold": 0.024
    },
}
```

# Shadow effect
```
{
    "shadow": True
}
```

# Shadow with custom paramters
```
{
    "shadow": {
        'blur': 15,
        'saturation': 40,
        'x_offset': 10,
        'y_offset': -5,
    }
}
```

# gradient with default params
```
{
    "gradient": True
}
```


# gradient with custom parameters
```
{
    "gradient": {
        'linear_direction': 45,
        'from_color': 'FF000000',
        'to_color': 'FFFFFF50',
        'stop_point': 0.5,
    }
}
```

# gradient to top direction

```
{
    "gradient": {
        'linear_direction': 'top',
        'from_color': 'green',
        'to_color': '00FF0010',
        'stop_point': 1,
    }
}
```

# trim with default params
```
{
    "trim": True
}
```

# trim with 5 threshold
```
{
    "trim": 5
}
```

# trim with 50 threshold
```
{
    "trim": 50
}
```

# blur with width 20 and custom rgba color
```
{
    "border": {
        "border_width": 20,
        "color": "FF00FF99"
    }
}
```

# blur with width 20 and red color
```
{
    "border": {
        "border_width": 20,
        "color": "red"
    }
}
```

# rotate with auto
```
{
    "rotate": "auto"
}
```

# rotate in clockwise direction
```
{
    "rotate": 90
}
```

# rotate in anti clockwise direction
```
{
    "rotate": -90
}
```

# flip by horizontal axis
```
{
    "flip": 'h'
}
```

# flip by horizontal and vertical axis
```
{
    "flip": 'h_v
}
```

# radius with 50 pixel
```
{
    "radius": 50
}
```

# radius for round corner
```
{
    "radius": "max"
}
```

# color replace by selecting dominant color
```
{
    "color_replace": {
        "to_color": "00FF00",
    },
}
```

# color replace with custom params
```
{
    "color_replace": {
        "to_color": "00FF00",
        "tolerance": 40,
        "from_color": "FF0000",
    },
}
```
